import axios from 'axios'
import React, { useEffect, useState } from 'react'

export default function PostDisplay(props) {
    // const [posts, setPosts] = useState([]);


    // useEffect(() => {
    //     let isMounted = true;
    //     if(isMounted){
    //         axios.get("http://localhost:8080/api/posts")
    //             .then(response => {
    //                 setPosts(response.data);
    //             })
    //             .catch(err => console.log(err));
    //     }
    //     return () => {isMounted = false}
    // }, [])


    return (
        <div>
            <ul id="post-list" className="text-start">
                {props.posts.slice(0).reverse().map((post, index) => {
                    return (
                        <li className="post py-1" key={index}>
                            <div className="post-container mx-2">
                                <h3><img src={post.profileImageUrl} className="profile-img mx-2"/>{post.firstName} {post.lastName} says:</h3>
                                <p className="ml-3">{post.message}</p>
                                <div className="text-center mb-3">
                                    {post.imageUrl != "" ?
                                        <img className="rounded post-img" src={post.imageUrl} />
                                        :
                                        null}
                                </div>
                            </div>
                            <hr />
                        </li>
                    )
                })}
            </ul>

        </div>
    )
}
