import axios from 'axios';
import React, { useState } from 'react'
import placeholderImg from '../Static/Images/placeholder-img.png'

export default function TweetForm() {
    const [message, setMessage] = useState("");

    function handleSubmit(e){
        e.preventDefault();

        const post={
            message: message
        }

        const currentId = localStorage.getItem("user_id");
        const getUserLink = "http://localhost:8080/api/users/" + currentId;
        //First, we make a call to the api to find the current logged-in user. Then we add that user to the post object.
        axios.get(getUserLink, currentId)
        .then(res => {
            post.user = res.data;
            axios.post("http://localhost:8080/api/posts/create", post)
            .then(response => console.log(response.data))
            .catch(error => console.log(error.response))
        })
        .catch(err => console.log(err));


    }


    return (
        <div id="tweet-form" className="pb-2">
            <div className="px-1 mt-2 d-flex align-items-start">
                <p id="tweet-form-profile-img-container">
                    <img src={placeholderImg} alt="" className="rounded-circle" id="tweet-form-profile-img"/>
                </p>
                <form className="w-100 mx-3 text-end" onSubmit={e => handleSubmit(e)}>
                    <textarea placeholder="What's happening?" className="w-100 bg-transparent border-0" rows="3" id="tweet-form-input" maxLength="200" value={message} onInput={e => setMessage(e.target.value)}/>
                    <button type="submit" className="btn btn-primary rounded-pill p-2" id="tweet-submit">Tweet</button>
                </form>
            </div>
        </div>
    )
}
