import React from 'react'
import Heart from 'react-animated-heart'
import { useState } from 'react';

export default function Post(props) {
    const [isClicked, setClicked] = useState(false);


    return (
        <div className="post-container mx-2">
            <h3><img src={props.post.profileImageUrl} className="profile-img mx-2" />{props.post.firstName} {post.lastName} says:</h3>
            <div className="container">
                <p className="ms-5">{post.message}</p>
                <div className="text-center mb-3">
                    {post.imageUrl != "" ?
                        <img className="post-img" src={post.imageUrl} />
                        :
                        null}
                </div>
                <div className="ms-5">
                    <span style={{ width: "0px" }}><Heart isClick={isClicked} onClick={() => setClicked(!isClicked)} /></span>
                </div>
            </div>
        </div>
    )
}
