import React from 'react'
import placeholderImg from '../Static/Images/placeholder-img.png'

export default function TweetForm() {

    function textAreaAdjust(element) {
        element.style.height = "1px";
        element.style.height = (25+element.scrollHeight)+"px";
    }



    return (
        <div>
            <div className="px-1 mt-2 d-flex align-items-center">
                <p id="tweet-form-profile-img-container">
                    <img src={placeholderImg} alt="" className="rounded-circle" id="tweet-form-profile-img"/>
                </p>
                <form className="w-75 mx-3">
                    <textarea onkeyup={textAreaAdjust(this)} placeholder="What's happening?" className="w-100 bg-transparent border-0" id="tweet-form-input"/>
                </form>
            </div>
        </div>
    )
}
