import axios from 'axios';
import React, { useState } from 'react'

export default function TweetForm(props) {
    const [message, setMessage] = useState("");
    const [messageError, setMessageError] = useState("");
    const [imageError, setImageError] = useState("");
    const [imageUrl, setImageUrl] = useState("");

    function handleSubmit(e){
        e.preventDefault();

        if(message.length !== 0){
            const post={
                message: message
            }

                axios.post("http://localhost:8080/api/posts/create/" + props.loggedInUser.userId, post)
                .then(response => {
                    let newPosts = [...props.posts];
                    newPosts.push(response.data);
                    props.setPosts(newPosts);
                    setMessage("");
                    setMessageError("");
                })
                .catch(error => console.log(error.response))
            } else {
            setMessageError("You can't make a blank anotter!");
        }        
    }


    const convertBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const fileReader = new FileReader();
            fileReader.readAsDataURL(file);

            fileReader.onload = (() => {
                resolve(fileReader.result)
            });

            fileReader.onerror = ((error) => {
                reject(error);
                setImageError("File is too large!")
            })

        })
    }

    const uploadImage = async (e) => {
        const file = e.target.files[0];
        const base64 = await convertBase64(file);
        setImageUrl(base64);
    }


    return (
        <div id="tweet-form" className="pb-2">
            <div className="px-1 mt-2 d-flex align-items-start">
                <p className="profile-img-container">
                    <img src={props.loggedInUser.profileImageUrl} alt="" className="rounded-circle profile-img"/>
                </p>
                <form className="w-100 mx-3 text-end" onSubmit={e => handleSubmit(e)}>
                    <textarea placeholder="What's happening?" className="w-100 bg-transparent border-0" rows="3" id="tweet-form-input" maxLength="200" value={message} onInput={e => {
                        setMessage(e.target.value);
                    }}/>
                    <p className="text-danger">{messageError}</p>
                    <svg viewBox="0 0 24 24" aria-hidden="true" class="r-1cvl2hr r-4qtqp9 r-yyyyoo r-z80fyv r-dnmrzs r-bnwqim r-1plcrui r-lrvibr r-19wmn03" style=""><g><path d="M19.75 2H4.25C3.01 2 2 3.01 2 4.25v15.5C2 20.99 3.01 22 4.25 22h15.5c1.24 0 2.25-1.01 2.25-2.25V4.25C22 3.01 20.99 2 19.75 2zM4.25 3.5h15.5c.413 0 .75.337.75.75v9.676l-3.858-3.858c-.14-.14-.33-.22-.53-.22h-.003c-.2 0-.393.08-.532.224l-4.317 4.384-1.813-1.806c-.14-.14-.33-.22-.53-.22-.193-.03-.395.08-.535.227L3.5 17.642V4.25c0-.413.337-.75.75-.75zm-.744 16.28l5.418-5.534 6.282 6.254H4.25c-.402 0-.727-.322-.744-.72zm16.244.72h-2.42l-5.007-4.987 3.792-3.85 4.385 4.384v3.703c0 .413-.337.75-.75.75z"></path><circle cx="8.868" cy="8.309" r="1.542"></circle></g></svg>

                    <button type="submit" className="btn btn-primary rounded-pill p-2" id="tweet-submit">Anotter</button>
                </form>
            </div>
        </div>
    )
}
