import React, { useEffect, useState } from 'react'
import { useHistory } from 'react-router';
export default function EditAccount(props) {
    const [email, setEmail] = useState(props.loggedInUser.email);
    const [firstName, setFirstName] = useState(props.loggedInUser.firstName);
    const [lastName, setLastName] = useState(props.loggedInUser.lastName);
    const [profileImageUrl, setProfileImageUrl] = useState(props.loggedInUser.profileImageUrl);
    const [imageError, setImageError] = useState("");
    const [biography, setBiography] = useState(props.loggedInUser.biography);


    let history = useHistory();

    function logout() {
        sessionStorage.clear();
        history.push("/");
    }

    useEffect(() => {
        console.log(props.loggedInUser);
    }, [])


    const convertBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const fileReader = new FileReader();
            fileReader.readAsDataURL(file);

            fileReader.onload = (() => {
                resolve(fileReader.result)
            });

            fileReader.onerror = ((error) => {
                reject(error);
                setImageError("File is too large!")
            })

        })
    }

    const uploadImage = async (e) => {
        const file = e.target.files[0];
        const base64 = await convertBase64(file);
        setProfileImageUrl(base64);
    }


    return (
        <>
            <h1 className="h1 mt-1 px-1" style={{ fontSize: "40px" }}>Edit Account Information</h1>
            <form onSubmit={() => console.log("submitting")} className="text-center d-flex flex-column align-items-center">
                <div className="container">
                    <div className="row bg-primary">
                        <div className="col">
                            <div className="form-group border-bottom my-3">
                                <h4 className="text-start">Edit Email</h4>
                                <input type="email" className="form-control signup-input" placeholder="Email" value={email} onInput={e => setEmail(e.target.value)} />
                                <p className="text-danger bold-font text-start">An error will go here</p>
                            </div>
                            <h4 className="text-start">Edit Name</h4>
                            <div className="row border-bottom">
                                <div className="col p-0" >
                                    <input type="text" className="form-control signup-input" placeholder="First name" value={firstName} onInput={e => setFirstName(e.target.value)} />
                                    <p className="text-danger bold-font text-start">An error will go here</p>
                                </div>
                                <div className="col-1"></div>
                                <div className="col p-0">
                                    <input type="text" className="form-control signup-input d" placeholder="Last name" value={lastName} onInput={e => setLastName(e.target.value)} />
                                    <p className="text-danger bold-font text-start">An error will go here</p>
                                </div>
                            </div>
                        </div>
                        <div className="col-sm-1"></div>
                        <div className="col">
                            <div className="form-group border-bottom border-left my-3">
                                <h4 className="text-start">Edit Profile Image <small style={{ fontSize: "12px" }}>(100 kb max)</small></h4>
                                <img src={profileImageUrl} className="profile-img-edit my-2" alt="current profile image" />
                                <input type="file" className="form-control" accept="image/*" onChange={e => uploadImage(e)} />
                                <p className="text-danger bold-font text-start">{imageError}</p>
                            </div>
                            <div className="form-group border-bottom my-3">
                                <h4 className="text-start">Edit Biography {"( " + biography.length + " / 200  )"}</h4>
                                <textarea placeholder={"Tell us about yourself, " + firstName} className="w-100 bg-transparent border-0" rows="2" id="tweet-form-input" maxLength="200" value={biography} onInput={e => {
                                    setBiography(e.target.value);
                                }} />
                                <p className="text-danger bold-font text-start">An error will go here</p>
                            </div>
                        </div>
                    </div>


                </div>


                <button type="submit" className="btn btn-light rounded-pill register-page-button w-75 p-55 mt-5 mb-3" id="signup-submit-button">Save Changes</button>
            </form>
        </>
    )
}
